function pageprocessorfinaidapp(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);

  var config = {
    "middlewareServerUrl": pagedef.middlewareServerUrl,
    "product": "banner"
  }
  config.tokenType = "oauth";
  $rootScope.demoMode = false;
  $rootScope.middlewareServerUrl = pagedef.middlewareServerUrl;

  $rootScope.demoMode = false;
  $rootScope.middlewareServerUrl = pagedef.middlewareServerUrl;

  /* Assign configuration params values to local variables */
  var middlewareUrl = pagedef.middlewareUrl;
  var licenseKey = pagedef.licenseKey;
  var yearsToShow = pagedef.yearsToShow;


  /* Declare page level variables  */
  var aidYearDropdownEndpoint = "/bannerfinaidyears";
  var finaidReqEndpoint = "/bannerfinaidstudentrequirements/aidy=";
  var finaidAwardOverviewEndpoint = "/bannerfinaidawardoverview/aidy=";
  var finaidAccountSummaryEndpoint = "/bannerfinaidaccountsummarybyterm";

  $scope.selTab = 1;
  $scope.title = "Financial Aid";

  function showAlert(msg) {
    if (window.device) {
      navigator.notification.alert(msg, null, $scope.title, null);
    } else {
      if ($rootScope.webapp) {
        $.alert({
          animation: 'none',
          title: $scope.title,
          content: msg,
          columnClass: 'col-lg-6 col-lg-offset-3 col-md-6 col-md-offset-3 col-sm-8 col-sm-offset-2 col-xs-10 col-xs-offset-1',
          buttons: {
            OK: {
              'btnClass': 'btn-primary boxradius popBtns',
              'action': function() {
                //alert('cancel');
              }
            }
          }
        });
      } else {
        alert(msg);
      }
    }
  }

  if (!pagedef.middlewareServerUrl) {
    showAlert("Middleware Url parameter should be set before proceeding !");
  }

  if (!pagedef.product) {
    showAlert("Product parameter should be set before proceeding.");
  }

  if (!yearsToShow || yearsToShow == "0") {
    yearsToShow = 0;
  }
  var title = "Financial Aid";
  $rootScope.appDisplayName = title;
  $scope.selyear = "";
  $scope.selyearDesc = "";
  $scope.selectedTab = 0;
  $scope.finaidreqs = null;
  $scope.awardoverview = null;
  $scope.accsummbyterm = null;
  $scope.accsummarypage = 1;

  $scope.previousPage = function() {
    $scope.accsummarypage = 1;

  }

  if(window.device) {
    $rootScope.back = function() {
      if ($scope.selTab == 3 && $scope.accsummarypage == 2) {
        //show acc summary info
        $scope.accsummarypage = 1;
        //$rootScope.showlogin = true;
      } else {
        $window.history.back();
      }
    } 
  }

  $scope.yearselect = function(index) {
    $scope.toggleClick();
    $scope.selyearDesc = $scope.finaidyears[index].DESCRIPTION;
    $scope.selyear = $scope.finaidyears[index].AIDY;

    $scope.finaidreqs = null;
    $scope.awardoverview = null;
    $scope.accsummbyterm = null;
    $scope.accsummarypage = 1;

    if ($scope.selTab == 1) {
      $scope.stureq();
    } else if ($scope.selTab == 2) {
      $scope.awards();
    } else {
      $scope.accsumm();
    }
  }


  //
  $scope.stureq = function() {
    $("#tab1").removeClass("toolbar-column-active");
    $("#tab2").removeClass("toolbar-column-active");
    $("#tab3").removeClass("toolbar-column-active");
    $("#tab1").addClass("toolbar-column-active");
    $scope.selTab = 1;
    //$rootScope.showlogin = true;
    if ($scope.selyear == "" || $scope.selyear == undefined) {
      $scope.finaidreqs = null;
      return;
    }

    if (!$scope.finaidreqs) {
      $rootScope.getAPI(config, finaidReqEndpoint + $scope.selyear, function(data) {
        if (data) {
          $scope.finaidreqs = data;
          angular.forEach($scope.finaidreqs.studentrequirements, function(value, key) {
            if (value.DESCRIPTION == "Free Application for Federal Student Aid (FAFSA)- Received") {
              value.DESCRIPTION = "Free Application for Federal Student Aid (FAFSA) - Received";
            }
          });
        } else {
          showAlert("No information is available for your account.")
        }
      });
    }

  }

  $scope.awards = function() {
    //$rootScope.showlogin = true;
    //$scope.selectedTab = 1;
    $("#tab1").removeClass("toolbar-column-active");
    $("#tab2").removeClass("toolbar-column-active");
    $("#tab3").removeClass("toolbar-column-active");
    $("#tab2").addClass("toolbar-column-active");
    $scope.selTab = 2;
    if ($scope.selyear == "" || $scope.selyear == undefined) {
      $scope.awardoverview = null;
      return;
    }

    if (!$scope.awardoverview) {
      $rootScope.getAPI(config, finaidAwardOverviewEndpoint + $scope.selyear, function(data) {
        if (data) {
          $scope.awardoverview = data;
          var coatotal = 0;
          // Calculate COA totals
          $.each(data.costattendance, function(cindex, coarec) {
            coatotal = coatotal + Number(coarec.AMOUNT);
          });
          // Award details
          var awards = data.awarddetails;
          var sortawards = awards;

          function SortByFundCode(x, y) {
            return ((x.RPRAWRD_FUND_CODE == y.RPRAWRD_FUND_CODE) ? 0 : ((x.RPRAWRD_FUND_CODE > y.RPRAWRD_FUND_CODE) ? 1 : -1));
          }


          sortawards.sort(SortByFundCode);
          var prevfund = null;
          var curfund = null;
          var funddesc = null;
          var prevfunddesc = null;
          var curstatus = null;
          var prevstatus = null;
          var totamt = 0;
          var fundsumm = [];
          var htmlscript = "";
          var found = false;
          var offeramt = 0;
          var prevofferamt = 0;
          $.each(sortawards, function(index, award) {
            found = true;
            curfund = award.RPRAWRD_FUND_CODE;
            funddesc = award.RFRBASE_FUND_TITLE;
            curstatus = award.RTVAWST_DESC;
            offeramt = Number(award.RPRATRM_OFFER_AMT);
            if (index == 0) {
              prevfund = curfund;
              prevfunddesc = funddesc;
              prevstatus = curstatus;
              prevofferamt = offeramt;
            }
            if (prevfund == curfund) {
              totamt = totamt + offeramt;
              //alert('total=' + totamt);
            } else {
              //alert('total:::' + totamt);
              fundsumm.push({
                "FUND": prevfund,
                "FUND_TITLE": prevfunddesc,
                "STATUS": prevstatus,
                "OFFERED": totamt
              });
              prevfund = curfund;
              prevfunddesc = funddesc;
              prevofferamt = offeramt;
              totamt = offeramt;
              //alert('offer total:::' + totamt);
            }

          });
          if (found) {
            fundsumm.push({
              "FUND": curfund,
              "FUND_TITLE": funddesc,
              "STATUS": curstatus,
              "OFFERED": totamt
            });
          }

          var awardgrandtot = 0;
          $.each(fundsumm, function(index, val) {
            awardgrandtot = awardgrandtot + Number(val.OFFERED);
          });
          $scope.awardoverview = {
            "needcalculation": data.CLASSLOOKUP[0],
            "housing": data.housing,
            "awarddetails": data.awarddetails,
            "costattendance": data.costattendance,
            "coatotal": coatotal,
            "cumulativeloaninfo": data.cumulativeloaninfo,
            "awardsummary": fundsumm,
            "awardsummtotal": awardgrandtot
          };
        } else {
          showAlert("No information is available for your account.")
        }
        //$location.path("/app/FinAid2/FinAid2Page3");
      });
    }

  }

  $scope.accsumm = function() {
    //$scope.selectedTab = 2;
    $("#tab1").removeClass("toolbar-column-active");
    $("#tab2").removeClass("toolbar-column-active");
    $("#tab3").removeClass("toolbar-column-active");
    $("#tab3").addClass("toolbar-column-active");
    $scope.selTab = 3;
    console.log("sel page:" + $scope.accsummarypage);
    if ($scope.accsummarypage == 1) {
      //$rootScope.showlogin = true;
    } else {
      $rootScope.showlogin = false;
    }

    if (!$scope.accsummbyterm) {
      $rootScope.getAPI(config, finaidAccountSummaryEndpoint, function(data) {
        if (data) {
          var prevterm = null;
          var curterm = null;
          var totcharges = 0;
          var totpayments = 0;
          var totbalance = 0;
          var accsummbyterm = [];
          var accsumm = null;
          $.each(data.accountbalancebyterm, function(index, val) {
            curterm = val.TBRACCD_TERM_CODE;
            if (curterm != prevterm) {
              prevterm = curterm;
              accsumm = {
                "TBRACCD_TERM_CODE": val.TBRACCD_TERM_CODE,
                "STVTERM_DESC": val.STVTERM_DESC,
                totc: Number(val.CHARGES),
                totp: Number(val.PAYMENT),
                totb: Number(val.BALANCE),
                accdetails: []
              };
              accsumm.accdetails.push(val);
              accsummbyterm.push(accsumm);
            } else {
              accsumm.accdetails.push(val);
              accsumm.totc = accsumm.totc + Number(val.CHARGES);
              accsumm.totp = accsumm.totp + Number(val.PAYMENT);
              accsumm.totb = accsumm.totb + Number(val.BALANCE);
            }
          });

          $scope.accsummbyterm = {
            "accountbalancebyterm": accsummbyterm,
            "accountbalancesummary": data.accountbalancesummary
          };
        } else {
          showAlert("No information is available for your account.")
        }
      });
    }

  }

  $scope.showtermdet = function(acc) {
    console.log("checking value of web app==>" + $rootScope.webApp)
      //if (!$rootScope.webApp) {
      //  $scope.accsummarypage = 1;
      //} else {
    $scope.accsummarypage = 2;
    //}

    $scope.termdesc = acc.STVTERM_DESC;
    $scope.acctdet = acc.accdetails;
    //$rootScope.showlogin = false;
  }

  $scope.formatDate = function(dt) {
    var t1 = dt.substring(0, 2);
    var t2 = dt.substring(3, 6).toLowerCase();
    var t3 = dt.substring(7, 11);
    var month = t2.substring(0, 1).toUpperCase() + t2.substring(1);
    return month + " " + t1 + ", " + t3;
  }

  $scope.toggleClick = function() {
    $('#dropdownMenu1').toggleClass('dropdownSelectBackground');
    $('#dropArrow').toggleClass('rotateImage');
  }


  function initApplet() {
    $rootScope.getAPI(config, aidYearDropdownEndpoint, function(data) {
      setTimeout(function() {
        if (data) {
          var aidYears = data.finaidyears;
          $scope.finaidyears = [];
          if (yearsToShow > 0) {
            angular.forEach(aidYears, function(value, key) {
              if (yearsToShow >= key || yearsToShow == 0) {
                $scope.finaidyears.push(value);
              }
            });
          } else {
            $scope.finaidyears = aidYears;
          }
          if ($scope.finaidyears && $scope.finaidyears.length > 0) {
            $scope.selyearDesc = $scope.finaidyears[0].DESCRIPTION;
            $scope.selyear = $scope.finaidyears[0].AIDY;
            $scope.yearselect(0);
            $scope.toggleClick();
          }


        } else {
          showAlert("No information is available for your account.")
        }
      }, 5)
    });
  }

  // Call the applet initialization code
  initApplet();

}

