function pageprocessorNews343(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    $("#fixedFooterContent").html('');
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    try {
  
      if ($rootScope.loggedIn) {
        $scope.userRoles = $rootScope.user.role.map(function(r) {
          return r.toLowerCase();
        });
      } else {
        $scope.userRoles = [];
      }
  
      console.log(JSON.stringify($scope.userRoles));
      $rootScope.newsFeeds = pagedef.newsFeeds;
      $rootScope.newsTitleNode = pagedef.title;
      $rootScope.newsDescNode = pagedef.desc;
      $rootScope.newsPubDateNode = pagedef.pubDate;
      $rootScope.newsImageNode = pagedef.img;
      $rootScope.newsLinkNode = pagedef.link;
      $rootScope.newsCustomParams = pagedef.customParams;
      $rootScope.newsParentNode = pagedef.parentNode;
      $rootScope.newsSubNode = pagedef.subNode;
      $rootScope.newsLocationNode = pagedef.location;
  
      var months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
  
      $scope.loadNewsFeeds = function(feed) {
        $.blockUI();
        var url = feed.feedUrl;
        $scope.selectedFeed = feed.label;
        if (window.device) {
          var feedUrl = url;
        } else {
          var feedUrl = "/websimulator/xml?url=" + url;
        }
        $http({
          method: 'GET',
          url: feedUrl
        }).then(function successCallback(resp) {
            var results = $.xml2json(resp.data);
            console.log(JSON.stringify(results));
            if ($rootScope.newsSubNode) {
              var feeds = results[$rootScope.newsParentNode][$rootScope.newsSubNode];
            } else {
              var feeds = results[$rootScope.newsParentNode];
            }
            console.log(feeds);
  
            if ($rootScope.newsPubDateNode) {
              feeds.sort(function(a, b) {
           if (new Date(b[$rootScope.newsPubDateNode]) > new Date(a[$rootScope.newsPubDateNode]))
           b[$rootScope.newsPubDateNode]=moment(b[$rootScope.newsPubDateNode]).format('ddd, DD MMM YYYY HH:mm:ss')
           a[$rootScope.newsPubDateNode]=moment(a[$rootScope.newsPubDateNode]).format('ddd, DD MMM YYYY HH:mm:ss')
           return 1;
         if (new Date(b[$rootScope.newsPubDateNode]) < new Date(a[$rootScope.newsPubDateNode]))
         b[$rootScope.newsPubDateNode]=moment(b[$rootScope.newsPubDateNode]).format('ddd, DD MMM YYYY HH:mm:ss')
         a[$rootScope.newsPubDateNode]=moment(a[$rootScope.newsPubDateNode]).format('ddd, DD MMM YYYY HH:mm:ss')  
         return -1;
                return 0;
              });
            }
           
            $scope.feeds = feeds;
            $rootScope.News343feeds = $scope.feeds;
            $.unblockUI();
          },
          function errorCallback(response) {
            console.log("Error : " + response);
            $.unblockUI();
            //   alert("error")
          });
      }
  
      console.log($rootScope.newsFeeds);
  
      $scope.checkUserHasAccess = function(role) {
        role = role.toLowerCase().split(',');
        var found = false;
        $scope.userRoles.forEach(function(val) {
          if (role.indexOf(val) != -1) {
            found = true;
          }
        });
        return found;
      }
  
      for (var i = 0; i < $rootScope.newsFeeds.length; i++) {
        var found = false;
        if ($scope.checkUserHasAccess($rootScope.newsFeeds[i].role) || $rootScope.newsFeeds[i].role.toLowerCase().trim() == 'public') {
          var found = true;
          $scope.loadNewsFeeds($rootScope.newsFeeds[i]);
          break;
        }
      }
      $scope.openFeed = function(index) {
        $rootScope.News343feedDetails = $scope.feeds[index];
        $rootScope.News343index = index;
        $scope.content = $rootScope.News343feedDetails.content;
        $location.path("/app/News343/News343Details");
      };
  
      $scope.trustHTML = function(html) {
        if (html.indexOf('meta') != -1) {
          return '';
        } else {
          try {
            var i = html.indexOf('<script');
            var j = html.indexOf('</script');
            console.log(i);
            console.log(j);
            if (i != -1 && j != -1) {
              html = html.substr(0, i) + " " + html.substr(j + 9);
            }
          } catch (e) {
            console.log(e)
          }
          console.log(html);
          var data = "<div>" + html + "</div>";
          return $sce.trustAsHtml(data);
        }
      }
  
      $scope.getFeedDate = function(f) {
        return f.date.getDate();
      };
  
      $scope.getFeedMonth = function(f) {
        return months[f.date.getMonth()];
      };
      $scope.date = new Date();
  
      $scope.getFeedTime = function(f) {
        var date = f.date.toString();
        date = date.split(" ")[4];
        return $scope.tConvert(date);
      };
  
      $scope.tConvert = function(time) {
        time = time.toString().match(/^([01]\d|2[0-3])(:)([0-5]\d)(:[0-5]\d)?$/) || [time];
  
        if (time.length > 1) {
          time = time.slice(1);
          time[5] = +time[0] < 12 ? ' AM' : ' PM';
          time[0] = +time[0] % 12 || 12;
        }
        return time.join('');
      }
  
      $scope.backgroundImage = function(feed) {
        if (feed.content.indexOf("img") != -1) {
          var url = feed.content.substring(feed.content.indexOf("src") + 5, feed.content.indexOf('"', feed.content.indexOf("src") + 5));
          return {
            'background': 'url(' + url + ') no-repeat',
            'background-size': 'cover',
            'height': '250px',
          }
        }
      }
    } catch (e) {
      alert(e)
    }
  
  }

