    var descriptionTemplate = "";
    var resultHtml = "";
    var id = "card-" + Math.random();
    var part1 = '<br><div id="' + id + '" class="cursorPointer list-group-item borderRadius4px clearfix marginBottom15px marginBottom10px appendUrlData" contenteditable="false" ><svg onclick="removeCard(' + id + ')" class="filledIcon12 themeColorSvgPath appendCross" xmlns="http://www.w3.org/2000/svg viewBox="0 0 30 30" > <g data - name="Layer 2" > <g data - name="30-cross" > <ath d="M17.65,15,29.45,3.2A1.87,1.87,0,1,0,26.8.55L15,12.35,3.2.55A1.87,1.87,0,0,0,.55,3.2L12.35,15,.55,26.8A1.88,1.88,0,0,0,1.88,30a1.83,1.83,0,0,0,1.32-.55L15,17.65l11.8,11.8a1.83,1.83,0,0,0,1.32.55,1.88,1.88,0,0,0,1.33-3.2Z" > < /path> </g> </g> </svg>';
    var part2 = '<div class="col-lg-9 col-xs-8 col-sm-9 paddingLeft10">';
    var part3 = '</div></div><br><div><br></div>';

    if (data.image) {
      imageTemplate = '<div class="col-lg-3 col-xs-4 col-sm-3 colPadding height100 cardImage" ng-if="">' + '<img alt="Data Image" src="' + data.image + '">' + '</div>';
    }

    if (data.title) {
      titleTemplate = '<div class="font16 colorDark toolbar-column-active proximaSemiBoldFont " contenteditable="false"><a href="' + validateUrl(data.url) + '" onclick="event.stopPropagation()" target="_blank" >' + data.title + '</a></div>';
    }

    if (data.description) {
      descriptionTemplate = '<div class="colorDarkGrey font12 ng-binding" contenteditable="false">' + data.description + '</div>';
    }

    var urlTemplate = '<div class="colorDarkGrey font12 ng-binding" contenteditable="false">' + validateUrl(data.url) + ' &nbsp</div>';

    if (imageTemplate) {
      resultHtml = part1 + imageTemplate;
    } else if (!imageTemplate) {
      resultHtml = part1;
    }

    resultHtml = resultHtml + part2;

    if (titleTemplate) {
      resultHtml = resultHtml + titleTemplate;
    }

    if (descriptionTemplate) {
      resultHtml = resultHtml + descriptionTemplate;
    }

    resultHtml = resultHtml + urlTemplate;
    resultHtml = resultHtml + part3;
    div = document.getElementById('postTitle');
    div.insertAdjacentHTML('beforeend', resultHtml);
    return resultHtml;
