      data: data,
      success: function success(data) {
        $scope.previewData = [].concat(_toConsumableArray(data), _toConsumableArray($scope.previewData));

        if (data.length > 0) {
          $scope.urlsForPreview(data);
        }
      },
      error: function error(e) {
        $.unblockUI();
      }
    });
  };

