function getDTStart(vevent) {
  var dtstart = "";
  $.each(vevent, function(key, val) {
    if (key.indexOf('DTSTART') === 0) {
      dtstart = val;
    }
  });
  return dtstart;
}


function iCalDateParser(date) {
  if (!date) return null;
  //if (!validateFormat(date)) return null;

  var year = date.substr(0, 4);
  var month = parseInt(date.substr(4, 2), 10) - 1;
  var day = date.substr(6, 2);
  var hour = date.substr(9, 2);
  var minute = date.substr(11, 2);
  var second = date.substr(13, 2);

  var clientDate = new Date(year, month, day, hour, minute, second);

  /*var offset = -5.0;
   utc = clientDate.getTime() + (clientDate.getTimezoneOffset() * 60000);

   serverDate = new Date(utc + (3600000*offset));

   //alert (serverDate.toLocaleString());


   console.log(year+" "+month+" "+day+"       "+serverDate);*/
  return clientDate;
}

/**
 * Check whether or not a given date is a valid iCal formatted date
 * @param {string} date
 * @return {boolean}
 */
function validateFormat(date) {
  var T_INDEX = 8;
  var Z_INDEX = 15;

  var d = date.split('');

  // Some basic checking
  if (!d instanceof String) return false;
  //if (d.length !== 16) return false;
  if (d[T_INDEX] !== 'T') return false;
  //if (d[Z_INDEX] !== 'Z') return false;

  // Return false if any of the expected numbers aren't numbers
  return d.every(function(char, i) {
    var charNotNumber = i !== T_INDEX && i !== Z_INDEX && isNaN(parseInt(char));
    return (charNotNumber) ? false : true;
  });




}

