        return data;
      }
  
      data = data.replace(/(?:\\r\\n|\\r|\\n)/g, '<br/>');
      data = data.replace(/(?:\\)/g, '');
      data = data.replace(/((http|https|ftp):\/\/[\w?=&.\/-;#~%-]+(?![\w\s?&.\/;#~%"=-]*>))/g, '<a href="$1" target="_blank">$1</a>');
      return $sce.trustAsHtml(data);
    }
  
    $scope.openFeed = function() {
      $location.path("/app/EventsiCal8/EventsiCal8");
      return;
    };
  
    $scope.next = function() {
  
      if ($scope.index === $scope.currentDateEvents.length - 1)
        return;
  
      $scope.index++;
      $routeParams.index++;
      $scope.selEvent = $scope.currentDateEvents[$scope.index];
  
      $window.scrollTo(0, 0);
    };
  
    $scope.prev = function() {
      if ($scope.index === 0)
        return;
  
      $scope.index--;
      $routeParams.index--;
      $scope.selEvent = $scope.currentDateEvents[$scope.index];
      $window.scrollTo(0, 0);
    };
  }

