  function getDateTimeOnTenantTimeZone(dt) {
    console.log("UNformatted date: " + dt);
    if (!dt) return null;
    let dtFormatted;
    let timeZoneVal;
    try {
      if (appletOverridedTimeZone && appletOverridedTimeZone != "null" && appletOverridedTimeZone.trim() != '') {
        timeZoneVal = appletOverridedTimeZone;
      } else {
        timeZoneVal = ($rootScope.user && $rootScope.user.timeZone) ? $rootScope.user.timeZone : '';
      }
      if (!timeZoneVal) {
        console.warn("Timezone is not configured for tenant, date values to be displayed in local timezone !");
      } else {
        dtFormatted = moment(dt).tz(timeZoneVal).format('DD MMM, YYYY, hh:mmA');
        console.log("Formatted date: " + dtFormatted);
        if (dtFormatted) {
          return dtFormatted;
        } else {
          console.error("Formatted date value is blank, some issue with configuration. Date values to be displayed in local timezone !");
        }
      }
    } catch (ex) {
      console.error("Moment library not supported, date values to be displayed in local timezone !");
    }
    var sentDate = new Date(dt);
    var timein12H = parseDateTime(sentDate).replace(":00", "").replace(":0", "");
    dtFormatted = sentDate.getDate() + " " + MONTHS[sentDate.getMonth()] + ", " + sentDate.getFullYear() + ", " + timein12H;
    console.log("**Formatted date: " + dtFormatted);
    return dtFormatted;
  }

  $scope.loadPage = function(page) {
    page++;

