            //console.log("6",$scope.messagingList);
            $.unblockUI();
            $scope.messagingList = $scope.messagingList.map(function(value, key) {
              value.shortMessage = value.defaultMessage.slice(0, 240);
              value.shortMessage = value.shortMessage.replace(/<br\/>/g, '\n');
              value.messageDetailLess = 0;
              if (!value.sent) {
                var today = new Date();
                var scheduleTime = new Date(value.scheduleTime);
                var timein12H, formattedDateStr;
                if (today > scheduleTime) {
                  if (value.createdDate == value.updatedDate) {
                    formattedDateStr = getDateTimeOnTenantTimeZone(value.createdDate);
                  } else {
                    formattedDateStr = getDateTimeOnTenantTimeZone(value.updatedDate);
                  }
                  formattedDateStr = "Sent - " + formattedDateStr;
                  value.messageStatus = "Sent";
                  value.messageTitle = formattedDateStr;
                } else {
                  formattedDateStr = "Scheduled - " + getDateTimeOnTenantTimeZone(value.scheduleTime);
                  value.messageStatus = "Scheduled";
                  value.messageTitle = formattedDateStr;
                }
              } else {
                formattedDateStr = "Sent - " + getDateTimeOnTenantTimeZone(value.createdDate);
                value.messageStatus = "Sent";
                value.messageTitle = formattedDateStr;
              }
              return value;
            });
          } else {
            $scope.messagingList = [];
            $scope.setMess = 1;
            //   console.log("newtest", $scope.setMess);
            //  console.log("newtest1", $scope.messagingList);
            $.unblockUI();
          }
        } else {
          // console.log("loding................");
          $.unblockUI();
          $scope.messagingList = $scope.messagingList.concat(res.data.content);
          if ($scope.messagingList.length > 0) {
            console.log("7", $scope.setMess);
            console.log("8", $scope.messagingList);
            $scope.messagingList = $scope.messagingList.map(function(value, key) {
              value.shortMessage = value.defaultMessage.slice(0, 240);
              value.messageDetailLess = 0;
              if (value.scheduledMsg) {
                var today = new Date();
                var scheduleTime = new Date(value.scheduleTime);
                var timein12H, formattedDateStr;
                if (today > scheduleTime) {
                  formattedDateStr = "Sent - " + getDateTimeOnTenantTimeZone(value.createdDate);
                  value.messageStatus = "Sent";
                  value.messageTitle = formattedDateStr;
                } else {
                  formattedDateStr = "Scheduled - " + getDateTimeOnTenantTimeZone(value.scheduleTime);
                  value.messageStatus = "Scheduled";
                  value.messageTitle = formattedDateStr;
                }
              } else {
                formattedDateStr = "Sent - " + getDateTimeOnTenantTimeZone(value.createdDate);
                value.messageStatus = "Sent";
                value.messageTitle = formattedDateStr;
              }
              return value;
            });
          } else {
            $scope.messagingList = [];
            $scope.setMess = 1;
            //  console.log("newtest3", $scope.setMess);
            //    console.log("newtest4", $scope.messagingList);
            $.unblockUI();
          }
        }
      }
    });

    var channelUrl = "/unifydplatform/channel?size=10&sort=channel";
