    $scope.updateRbacPermisssions = function() {
      var channelsWithRbac = [];
      angular.forEach($rootScope.channels, function(value, key) {
        if (value.channelId == "connect" && value.enable) {
          value['enable'] = (rbacPermissions['3098835178:Connect'] != undefined) ? rbacPermissions['3098835178:Connect'] : false;
        } else if (value.channelId == "email" && value.enable) {
          value['enable'] = (rbacPermissions['2244103068:Email'] != undefined) ? rbacPermissions['2244103068:Email'] : false;
        } else if (value.channelId == "inappnotification" && value.enable) {
          value['enable'] = (rbacPermissions['2247827164:Inapp'] != undefined) ? rbacPermissions['2247827164:Inapp'] : false;
        } else if (value.channelId == "notification" && value.enable) {
          value['enable'] = (rbacPermissions['2150936346:Push'] != undefined) ? rbacPermissions['2150936346:Push'] : false;
        } else if (value.channelId == "sms" && value.enable) {
          value['enable'] = (rbacPermissions['2147597657:SMS'] != undefined) ? rbacPermissions['2147597657:SMS'] : false;
        }
        channelsWithRbac.push(value);
      });
      $rootScope.channels = channelsWithRbac;
