          //var scheduleDate = new Date($('#datetimepicker11')[0].value);
          var scheduleDate;
          if ($rootScope.webApp) {
            scheduleDate = new Date($('#datetimepicker11')[0].value);
          } else {
            scheduleDate = new Date($('#datetimepicker11').text());
          }

          if (scheduleDate.getTime() < currentDate.getTime()) {
            $scope.showAlert('Scheduled time should be greater that current time.');
            return
          }

          if (scheduleDate.getTime() - currentDate.getTime() < 1800000) {
            //console.log(scheduleDate.getTime());
            //console.log("H " + scheduleDate.getHours() + " M " + scheduleDate.getMinutes() + " S" + scheduleDate.getSeconds());
            //console.log(currentDate.getTime());
            //console.log("H " + currentDate.getHours() + " M " + currentDate.getMinutes() + " S" + currentDate.getSeconds());
            $scope.showAlert('Scheduled time should be at least 30 minutes.');
            return
          }
          messageBody['scheduleTime'] = scheduleDate;
