        if ($rootScope.selectedChannelsMessaging.indexOf('sms') !== -1) {
          channelMsg['sms'] = $('#smsMessage').val();
          messageBody.defaultMessage = $rootScope.selectedChannelsMessaging.length > 1 ? $scope.description : channelMsg['sms'];
