          channelMsg['notification'] = $('#notificationMessage').val();
          messageBody.defaultMessage = $rootScope.selectedChannelsMessaging.length > 1 ? $scope.description : channelMsg['notification'];
        }


        messageBody['channelMsg'] = channelMsg;

        if ($scope.imageSource && $scope.imageSource === 'camera') {
          $.blockUI();
          var url = "https://kryptosda.kryptosmobile.com/kryptosds/api/imageUpload";
          var data = {
            'imageData': $scope.postImage,
            "imageExtn": 'png'
          };
          if ($scope.editSchduleMessageImage) {
            messageBody['graphicType'] = 'image';
            messageBody['graphicUrl'] = $scope.postImage;
            messageBody['graphicName'] = $scope.postContentTitle;
            $scope.savePost(messageBody);
          } else {
            $http.post(url, data).then(function(resp) {
              if (resp.data.url) {
                messageBody['graphicType'] = 'image';
                messageBody['graphicUrl'] = resp.data.url;
                messageBody['graphicName'] = $scope.postContentTitle;
                $scope.savePost(messageBody);
              } else {
                $.unblockUI();
              }
            });
          }
        } else if ($scope.imageSource && $scope.imageSource === 'youtube') {
          messageBody['graphicType'] = 'video';
          messageBody['graphicUrl'] = $scope.videoId;
          messageBody['graphicName'] = $scope.postContentTitle;
          $scope.savePost(messageBody);
        } else {
          messageBody['graphicType'] = '';
          messageBody['graphicUrl'] = '';
          messageBody['graphicName'] = "";
          $scope.savePost(messageBody);
        }
      }
    };

    $scope.savePost = function(messageObject) {


      var dmsg = messageObject.defaultMessage;
      var wmsg = messageObject.channelMsg.whatsUp;
      var smsg = messageObject.channelMsg.sms;
      var nmsg = messageObject.channelMsg.notification;
      var emsg = messageObject.channelMsg.email;



      wmsg = wmsg ? wmsg.replace(/<br\s*\/?>/gi, ' <br>') : '';
      wmsg = wmsg ? wmsg.replace(/(http:\/\/[^\s]+)/gi, '<a href="$1" target="blank" >$1</a>') : '';
      wmsg = wmsg ? wmsg.replace(/(https:\/\/[^\s]+)/gi, '<a  href="$1" target="blank" >$1</a>') : '';

      messageObject['channelMsg']['whatsUp'] = wmsg;

      /*smsg = smsg ? smsg.replace(/<br\s*\/?>/gi, ' <br>') : '';
          smsg = smsg ? smsg.replace(/(http:\/\/[^\s]+)/gi, '<a href="$1">$1</a>') : '';
          smsg = smsg ? smsg.replace(/(https:\/\/[^\s]+)/gi, '<a href="$1">$1</a>') : '';
    
          messageObject['channelMsg']['sms'] = smsg;*/

      /*nmsg = nmsg ? nmsg.replace(/<br\s*\/?>/gi, ' <br>') : '';
          nmsg = nmsg ? nmsg.replace(/(http:\/\/[^\s]+)/gi, '<a href="$1">$1</a>') : '';
          nmsg = nmsg ? nmsg.replace(/(https:\/\/[^\s]+)/gi, '<a href="$1">$1</a>') : '';
    
          messageObject['channelMsg']['notification'] = nmsg;*/


      emsg = emsg ? emsg.replace(/<br\s*\/?>/gi, ' <br>') : '';
      emsg = emsg ? emsg.replace(/(http:\/\/[^\s]+)/gi, '<a  href="$1" target="blank">$1</a>') : '';
      emsg = emsg ? emsg.replace(/(https:\/\/[^\s]+)/gi, '<a  href="$1" target="blank">$1</a>') : '';

      messageObject['channelMsg']['email'] = emsg;


      dmsg = dmsg ? dmsg.replace(/<br\s*\/?>/gi, ' <br>') : '';
      dmsg = dmsg ? dmsg.replace(/(http:\/\/[^\s]+)/gi, '<a href="$1" target="blank">$1</a>') : '';
      dmsg = dmsg ? dmsg.replace(/(https:\/\/[^\s]+)/gi, '<a href="$1" target="blank">$1</a>') : '';

      messageObject['defaultMessage'] = dmsg;


      if (!$rootScope.updateMessage) {

        console.log("new post test", messageObject);

        var url = '/unifydmessaging/postMessage';
        $rootScope.callAPI(url, 'POST', messageObject, function(res) {
          $rootScope.selectedUserMessaging = [];
          $rootScope.selectedGenderMessaging = [];
          $rootScope.selectedGroupMessaging = [];
          $rootScope.selectedUsersMessaging = [];
          $rootScope.selectedChannelsMessaging = [];
          $rootScope.groupsMessaging = null;
          $rootScope.gendersMessaging = null;
          $rootScope.channels = null;
          $rootScope.categoryMessaging = null;
          $scope.clearImage();
          $location.path('/app/MessageModule/MessageModule');
          $scope.$apply();
        })

      } else {

        var url = '/unifydmessaging/updateMessage';
        var updateMessage = messageObject;
        updateMessage["id"] = $rootScope.msgToEdit.id;
        updateMessage["createdBy"] = $rootScope.msgToEdit.createdBy;
        updateMessage["createdDate"] = $rootScope.msgToEdit.createdDate;

        //console.log("new hard test",updateMessage);
        $rootScope.callAPI(url, 'POST', updateMessage, function(res) {
          $rootScope.selectedUserMessaging = [];
          $rootScope.selectedGenderMessaging = [];
          $rootScope.selectedGroupMessaging = [];
          $rootScope.selectedUsersMessaging = [];
          $rootScope.selectedChannelsMessaging = [];
          $rootScope.groupsMessaging = null;
          $rootScope.gendersMessaging = null;
          $rootScope.channels = null;
          $rootScope.categoryMessaging = null;
          $scope.clearImage();
          $location.path('/app/MessageModule/MessageModule');
          $scope.$apply();
        })
      }

    };
