    $scope.descriptionChange = function() {
      $scope.editMessage = '~';
    };
    var specialChars = "<>!#$%^&*()+[]{}?:;|'\"\\,/~`=";
    $scope.checkForSpecialChar = function(string) {
      for (i = 0; i < specialChars.length; i++) {
        if (string.indexOf(specialChars[i]) > -1) {
          return true
        }
      }
      return false;
    }

    $scope.searchUser = function(page, event) {
      $('#msgNoDataFound').show();
      $scope.showErrorMessage = false;
      $scope.errorMessage = "";
      $scope.userSearchResult.users = "";

      if (event) {
        event.stopPropagation();
        event.preventDefault();
      }
      if ($scope.userSearchEmail.length < 3) {
        $scope.showErrorMessage = true;
        $scope.errorMessage = "Please enter at least 3 characters";
        return false;
      }
      if ($scope.checkForSpecialChar($scope.userSearchEmail)) {
        $scope.showErrorMessage = true;
        $scope.errorMessage = "No data found";
        return;
      }

      if (!page)
        page = 0;
      else
        page++;


      var requestData = {};
      requestData.rbacGroups = $scope.userRbacGroups;
      var userStr = $scope.userSearchEmail.toLowerCase();
      var url = '/unifydidentity/user/search/messagingmodule/findByName?sort=email,ASC&name=' + userStr + '&page=' + page;
      /*var url = '/unifydidentity/user/search/findByEmailStartingWith?sort=email,ASC&email=' + userStr + '&page=' + page; */

      $rootScope.isblocking = true;
      $rootScope.callAPI(url, 'POST', requestData, function(res) {
        $rootScope.isblocking = false;
        var data = res.data;
        console.log("data.content.length", data.content.length);
        if (data.content.length == 0) {
          $scope.showErrorMessage = true;
          $scope.errorMessage = "No data found";
        }
        console.log("data", data);
        $scope.userSearchResult.users = data['content'];
        $scope.userSearchResult.page = data['number'];
        $scope.userSearchResult.last = data['last'];
      })

    };


    $scope.searchUsersMatching = function() {
      var str = $scope.userSearchEmail;
      var queryParam = ($scope.external) ? "&external=external" : "";
      var url = "/unifydidentity/open/users?userId=" + str + queryParam;
      $rootScope.callAPI(url, 'GET', {}, function(res) {
        $scope.userSearchResult.users = (res && res.data) ? res.data : [];
        $scope.userSearchResult.last = true;
        $scope.userSearchResult.page = 1;
      })
    }


    $scope.validatePost = function() {


      if ($rootScope.selectedChannelsMessaging.length === 0) {
        $scope.showAlert('Select at least one channel.');
        return false;
      }

      if ($rootScope.selectedChannelsMessaging.indexOf('email') !== -1 && !$scope.title) {
        $scope.showAlert('Enter subject for the post.');
        return false;
      }

      if ($rootScope.selectedChannelsMessaging.indexOf('connect') !== -1 && !$scope.description && !$scope.postImage) {
        $scope.showAlert('Enter message or upload media for the post.');
        return false;
      }

      if ($rootScope.selectedChannelsMessaging.indexOf('notification') !== -1 && !$scope.title) {
        $scope.showAlert('Enter title for the post.');
        return false;
      }

      if (!$scope.description) {
        $scope.showAlert('Enter message for the post.');
        return false;
      }

      return true;

