        if (this.files[0].name.split('.').pop().toLowerCase() == 'png' || this.files[0].name.split('.').pop().toLowerCase() == 'jpg' || this.files[0].name.split('.').pop().toLowerCase() == 'jpeg') {
          var fileSize = this.files[0].size / 1024 / 1024;
          console.log("File Size is=" + fileSize);
          if (fileSize > 5) {
            console.log("File size should not exceed 5MB, selected file size = " + fileSize);
            alert("File size should not exceed 5MB.");
            return;
          }

          var FR = new FileReader();
          FR.onload = function(e) {
            var base64DataSize = e.target.result.length / 1024 / 1024;
            if (base64DataSize > 5) {
              console.log("File size exceeds the 5MB limit when converted to Base64 data, Base64 file size = " + fileSize);
              alert("File size exceeds the 5MB limit when converted to Base64 data.");
              return;
            }
            $scope.postImage = e.target.result;
            $scope.imageSource = 'camera';
            $('#postImageDiv').attr("src", $scope.postImage);
            $scope.postContentTitle = file.name;
            $scope.$apply();
          };
          FR.readAsDataURL(this.files[0]);
