  $scope.calculateGPA = function() {
    $scope.semesterGPA = 0;
    $scope.cumulativeGPA = 0;
    var totcredits = 0;
    var totgradeval = 0;
    var totgpaVal = 0;
    var nval = 0;
    var ngrade = 0;
    var ngpoint = 0;
    console.log("you are here");
    angular.forEach($scope.classes, function(value, key) {
      nval = 0;
      ngrade = 0;
      if (value.credits && !isNaN(value.credits)) {
        console.log("credits=" + value.credits + " - " + isNaN(value.credits));
        try {
          nval = parseInt(value.credits);
        } catch (ex) {
          nval = 0;
        }
        if (value.grade) {
          ngrade = $scope.getGradePoint(value.grade);
        }
        ngpoint = nval * ngrade;
        console.log("ngpoint=" + ngpoint);
        if (ngpoint > 0) {
          value.classGPA = ngpoint;
        } else {
          value.classGPA = "0";
        }
        console.log("still here");
        totcredits += nval;
        totgradeval += ngpoint;
      } else if (value.credits == "0") {
        console.log("credits zero");
        value.classGPA = "0";
      }
    });
    console.log("totcredits=" + totcredits);
    console.log("totgradeval=" + totgradeval);
    if (totcredits > 0) {
      totgpaVal = totgradeval / totcredits;
    } else {
      totgpaVal = 0;
    }
    $scope.semesterGPA = totgpaVal;
    // $scope.cumulativeGPA = 0;
    if (!isNaN($scope.cumGPA) && !isNaN($scope.cumCredits)) {
      totgradeval += ($scope.cumGPA * $scope.cumCredits);
      totcredits += Number($scope.cumCredits);
      $scope.cumulativeGPA = totgradeval / totcredits;

      if (isNaN($scope.cumulativeGPA)) {
        $scope.cumulativeGPA = 0
      }
    }

    console.log("checking value semester==>" + $scope.semesterGPA)
    console.log("checking value cumulative==>" + $scope.cumulativeGPA)

  }

