  $scope.profileCrop = function() {

    $scope.canvas = document.getElementById('Canvasss');
    $scope.context = $scope.canvas.getContext('2d');

    //$scope.$digest(function(){

    $scope.imgWidth = $("#canvas").width() + 10;
    $scope.imgHeight = $("#canvas").height() + 10;
    $scope.imgPos = $("#canvas").position();

    $scope.sourceX = $scope.imgPos.left;
    $scope.sourceY = $scope.imgPos.top;

    $scope.sourceWidth = $scope.imgWidth;
    $scope.sourceHeight = $scope.imgHeight;

    if ($(window).width() <= 768) {
      $scope.destWidth = 130;
      $scope.destHeight = 130;
      $scope.context.fillRect(0, 0, 130, 130);
    } else {
      $scope.destWidth = 146;
      $scope.destHeight = 146;
      $scope.context.fillRect(0, 0, 146, 146);
    }

    $scope.destX = 0;
    $scope.destY = 0;

    $scope.imageObj.src = $(".ModalCropImage").attr("src");
    $scope.context.fillStyle = 'white';
    $scope.context.drawImage($scope.imageObj, $scope.sourceX, $scope.sourceY, $scope.sourceWidth, $scope.sourceHeight, $scope.destX, $scope.destY, $scope.destWidth, $scope.destHeight);

    $scope.getBase64 = $scope.canvas.toDataURL();
